<?php
/**
 * Test class for ZFDate_DateHelper.
 * Generated by PHPUnit on 2010-12-02 at 22:09:17.
 */
class ZFDate_DateHelperTest extends ControllerTestCase
{
    /**
     * @var ZFDate_DateHelper
     */
    protected $object;

    /* (non-PHPdoc)
     * @see Framework/PHPUnit_Framework_TestCase::setUp()
     */
    public function setUp()
    {
    	parent::setUp();
        $this->object = ZFDate_DateHelper::getInstance();
    }

    /* (non-PHPdoc)
     * @see Framework/PHPUnit_Framework_TestCase::tearDown()
     */
    public function tearDown()
    {
    	parent::tearDown();
    }

    public function testIsValidDate()
    {
    	$result = $this->object->isValidDate('2009-01-01');
    	$this->assertTrue($result,'It is a valid date');
    }
    
    public function testIsValidDateFails()
    {
    	$result = $this->object->isValidDate('not a date');
    	$this->assertFalse($result,'This is not a valid date');
    }
}
?>
