<?php
/**
 * Test class for ZFReport_Form_Report.
 * Generated by PHPUnit on 2010-10-27 at 22:19:19.
 */
class ZFReport_Form_ReportTest extends ControllerTestCase
{
    /**
     * @var ZFReport_Form_Report
     */
    protected $object;

    /**
     * Sets up the fixture, for example, opens a network connection.
     * This method is called before a test is executed.
     */
    public function setUp()
    {
    	parent::setUp();
        $this->object = new ZFReport_Form_Report();
    }

    /**
     * Tears down the fixture, for example, closes a network connection.
     * This method is called after a test is executed.
     */
    public function tearDown()
    {
    	parent::tearDown();
    }

    public function testPrepareDateFilters()
    {
    	$this->object->setUseDateFilters(true);
    	$this->object->setForm();
    	$this->assertNotNull($this->object->getElement('dateTo'),'Date to is null');
    	$this->assertNotNull($this->object->getElement('dateFrom'),'Date from is null');
    	$this->assertType('Zend_Form_Element_Text',$this->object->getElement('dateTo'));
    	$this->assertType('Zend_Form_Element_Text',$this->object->getElement('dateFrom'));
    }

    public function testPrepareDateFiltersNotSetWillNotDisplayFilters()
    {
    	$this->object->setForm();
    	$this->assertNull($this->object->getElement('dateTo'),'Date to is not null');
    	$this->assertNull($this->object->getElement('dateFrom'),'Date from is not null');
    }

    /**
     * @internal This test may seem lame, but you will want to have a
     * submit disregarding if the user put the columns or the date filters
     */
    public function testSubmitWillBeShown()
    {
    	$this->object->setForm();
    	$this->assertNotNull($this->object->getElement('submit'),'The submit element is empty');
    }

    public function testSetCustomLegend()
    {
    	$this->object->setMyLegend();
    	$this->object->setForm();
    	$this->assertNotNull($this->object->getLegend(),'Legend is not set');
    	$this->assertEquals('report', $this->object->getLegend());
    }
}
?>
