class Applicant_Library_Reports_PaidFeesTest extends ControllerTestCase
{
    /* (non-PHPdoc)
     * @see Framework/PHPUnit_Framework_TestCase::setUp()
     */
    public function setUp()
    {
    	parent::setUp();
    	$this->dataSetStackBuffer = array('applicantReport'=>1);
		$this->loadDataSets();
    }

    /* (non-PHPdoc)
     * @see Framework/PHPUnit_Framework_TestCase::tearDown()
     */
    public function tearDown()
    {
    	$this->unLoadDataSets();
    	parent::tearDown();
    }

    public function testRunReport()
    {
    	
    	$report = new Applicant_Library_Reports_PaidFees(array('dateFrom'=>'2010-03-01','dateTo'=>'2010-03-28'));
    	$report->init();
    	$records = $report->runReport(false);
    	$this->assertGreaterThanOrEqual(1,count($records));
    }

    public function testCacheData()
    {
    	
		$report = new Applicant_Library_Reports_PaidFees(array('signaled'=>false,'dateFrom'=>'2010-01-01','dateTo'=>'2010-12-28'));
		$report->init();
		$result = $report->runReport(true);
		$this->assertGreaterThanOrEqual(1,count($result));
		$result = $report->runReport(true);
		$this->assertFalse($report->getSignaled());
    }
}
