	public function testConfigureCache()
	{
		$object = new Financial_Library_Reports_DueRefunds(array('cacheIdentifier'=>'dueRefunds'));
		$object->setCacheFrontEnd('Core');
		//			$object->setCacheBackEnd('Apc');
