	}

	public function testHandleQueryParamsBadPagination()
	{
		$args = array('fakee'=>2);
		$object = new Financial_Library_Reports_DueRefunds($args);
		$object->prepareCacheSeed();
		$this->assertEquals('dueRefundsReport',$object->getSeed());
		unset($object);
		$args = array('page'=>"x3");
		$object = new Financial_Library_Reports_DueRefunds($args);
