	public function testInitDb()
	{
		$object = new Financial_Library_Reports_DueRent;
		$object->setGatewayLink(Financial_Library_Reports_DueRent::DB);
		$db = $object->getGatewayLink();
		$this->assertType('Zend_Db_Adapter_Pdo_Mysql', $db);
