<?php
/**
 * Test class for Unit_Library_SearchHelper.
 * Generated by PHPUnit on 2010-11-07 at 21:04:01.
 */
class Unit_Library_SearchHelperTest extends ControllerTestCase
{
	/* (non-PHPdoc)
	 * @see Framework/PHPUnit_Framework_TestCase::setUp()
	 */
	public function setUp()
	{
		parent::setUp();
		$this->dataSetStackBuffer = array('apartmentsUnitsAndModels'=>1);
		$this->loadDataSets();
		$this->object = new Unit_Library_SearchHelper;
	}

	/* (non-PHPdoc)
	 * @see Framework/PHPUnit_Framework_TestCase::tearDown()
	 */
	public function tearDown()
	{
		$this->unLoadDataSets();
		parent::tearDown();
	}

	public function testSearchWithNoArgsReturnsResult()
	{
		$args = array('size'=>'175-g','beds'=>'any','baths'=>'any','floors'=>'any');
		
		$result = $this->object->searchUnit($args);
		$this->assertTrue(is_array($result),'We expected an array');
		$this->assertGreaterThanOrEqual(1, count($result),'We should have at least one result');
	}
}
